import React, { Component } from 'react';
//import {getSession} from '@mysql/xdevapi';
import './GameBoard.css'
import mysql from './mysql'
import HelpPopup from './HelpPopup';
import Instance from './Instance';
import Solution from './Solution';
import Pair from "../game/Pair";

class GameBoard extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            helpButtonPopUp: false,
            dataInstance : "",
        };
        this.initDb();
    }

    setTrigger = () =>{
        this.setState({helpButtonPopUp : !this.state.helpButtonPopUp})        
    }


    initDb(){
        const connection = mysql.createConnection({
            password: '86Ex$y3s',
            user: 'pcp-user',
            host: 'pcp.digitelstudios.lu',
            port : '3306',
            database: 'pcp-db'
        })

        connection.connect()

        connection.query('SELECT * FROM instances AS solution', (err, rows, fields) => {
          if (err) throw err

          this.setState({dataInstance : rows[0].pairs})

          console.log('The solution is: ', rows[0].pairs)
        })

        connection.end()
    }


    render(){
        return(
            <main>
                <div>                
                    <button className='gameButtons'>Load Instance</button>
                    <button className='gameButtons'>Start playing</button>
                    <button className='gameButtons' onClick={this.setTrigger}>Help ?</button>
                </div>
                    <div>
                        <Instance data={this.state.dataInstance}/>
                        {this.state.helpButtonPopUp && 
                        <HelpPopup setTrigger={this.setTrigger} text={"text test"}/>}
                        <div className='rectangle'>


                    </div>                                     
                </div>
            </main>

        );
    }
}

export default GameBoard
